mapper.select:
SELECT log.id, log.product_id AS productId, p.name AS productName, log.old_price AS oldPrice, log.new_price AS newPrice, log.operate_user_id AS operateUserId, u.nickname AS operateUserNickname, log.operate_time AS operateTime, log.operate_type AS operateType FROM product_price_log log LEFT JOIN product p ON log.product_id = p.id LEFT JOIN user u ON log.operate_user_id = u.id WHERE log.product_id = #{params.productId}
AND log.operate_time >= #{params.startTime}
AND log.operate_time <= #{params.endTime}
ORDER BY log.operate_time DESC

SELECT log.id, log.product_id AS productId, p.name AS productName, p.shop_id AS shopId, s.name AS shopName, log.old_price AS oldPrice, log.new_price AS newPrice, log.operate_user_id AS operateUserId, u.nickname AS operateUserNickname, log.operate_time AS operateTime, log.operate_type AS operateType FROM product_price_log log LEFT JOIN product p ON log.product_id = p.id LEFT JOIN shop s ON p.shop_id = s.id LEFT JOIN user u ON log.operate_user_id = u.id
AND log.product_id = #{params.productId}
AND p.shop_id = #{params.shopId}
AND log.operate_user_id = #{params.operateUserId}
AND log.operate_time >= #{params.startTime}
AND log.operate_time <= #{params.endTime}
ORDER BY log.operate_time DESC
